INSERT INTO "comments" VALUES (5977, 26617075, '', 1, 7);
INSERT INTO "comments" VALUES (5978, 26617075, '抬走，下一个。', 1, 29);
INSERT INTO "comments" VALUES (5979, 26617075, '主角还是请一些演技咖吧，比如之前的秦昊，鬼吹灯的潘粤明。要不就选好看的人，至少赏心悦目，愿意多看两眼。', 1, 3);
INSERT INTO "comments" VALUES (5980, 26617075, '一集劝退', 1, 0);
INSERT INTO "comments" VALUES (5981, 26617075, '别搞那么尴尬好不好，云顶天宫小说很精彩的', 1, 0);
