INSERT INTO "comments" VALUES (12174, 35472124, '人生就是不断告别的过程，会变好的。', 5, 3);
INSERT INTO "comments" VALUES (12175, 35472124, '第一季到第二季的完美过渡 和普吉岛告别 我圆满了', 5, 5);
